const EMAIL_DOMAIN = "example.com"; // بدّل هذا لاحقًا لو عندك دومين بريد حقيقي

function getUser() { try { return JSON.parse(localStorage.getItem('user')) || null; } catch(e) { return null; } }
function setUser(u) { localStorage.setItem('user', JSON.stringify(u)); }
function logout() { localStorage.removeItem('user'); renderAuth(); }

function renderAuth() {
  const u = getUser();
  const form = document.getElementById('loginForm');
  const info = document.getElementById('userInfo');
  const nameSpan = document.getElementById('userName');
  if (u) { form.classList.add('hidden'); info.classList.remove('hidden'); nameSpan.textContent = u.name + " (" + u.email + ")"; }
  else { form.classList.remove('hidden'); info.classList.add('hidden'); }
}

function mailtoFor(idx, name) {
  const local = name.replace(/\s+/g,'').toLowerCase().replace(/[^\u0600-\u06FFa-z0-9]/g,'');
  const to = encodeURIComponent(local + idx + "@" + EMAIL_DOMAIN);
  const subject = encodeURIComponent("رسالة إلى " + name);
  const u = getUser();
  const who = u ? (u.name + " — " + u.email) : "زائر";
  const body = encodeURIComponent("مرحباً " + name + "\n\n[اكتب رسالتك هنا]\n\n—\nالمرسل: " + who);
  return `mailto:${to}?subject=${subject}&body=${body}`;
}

function renderCards() {
  const root = document.getElementById('cards');
  root.innerHTML = DATA.map((p, i) => `
    <div class="card">
      <img src="${p.photo}" alt="${p.name}">
      <div class="body">
        <div class="name">${p.name}</div>
        <div class="meta">الهواية: ${p.hobby}</div>
        <a href="#" class="btn" data-i="${i+1}" data-name="${p.name}">راسلها</a>
      </div>
    </div>
  `).join('');

  root.querySelectorAll('.btn').forEach(btn => {
    btn.addEventListener('click', (e) => {
      e.preventDefault();
      const u = getUser();
      if (!u) { alert("سجّل دخول أولاً"); return; }
      const idx = btn.getAttribute('data-i');
      const name = btn.getAttribute('data-name');
      window.location.href = mailtoFor(idx, name);
    });
  });
}

document.addEventListener('DOMContentLoaded', () => {
  document.getElementById('loginForm').addEventListener('submit', (e) => {
    e.preventDefault();
    const name = document.getElementById('loginName').value.trim();
    const email = document.getElementById('loginEmail').value.trim();
    if (!name || !email) { alert("اكتب اسمك وإيميلك"); return; }
    setUser({name, email});
    renderAuth();
  });
  document.getElementById('logoutBtn').addEventListener('click', (e)=>{e.preventDefault(); logout();});
  renderAuth();
  renderCards();
});
